def interpolation_search(arr, low, high, x):
    if low <= high:
        mid = low + ((high - low) // (arr[high] - arr[low])) * (x - arr[low])
        if arr[mid] == x:
            return mid
        elif arr[mid] < x:
            return interpolation_search(arr, mid + 1, high, x)
        else:
            return interpolation_search(arr, low, mid - 1, x)
    else:
        return -1

arr = [10, 12, 13, 16, 18, 19, 20, 21, 22, 33, 35, 42, 47]
x = 18

result = interpolation_search(arr, 0, len(arr) - 1, x)

if result != -1:
    print("Element is present at index", str(result))
else:
    print("Element is not present in array")
