class Node:
    def __init__(self, x):
        self.val = x
        self.next = None
        self.random = None

def copyRandomList(head):
    if not head:
        return None

    old_to_new = {}
    cur = head
    while cur:
        old_to_new[cur] = Node(cur.val)
        cur = cur.next

    cur = head
    while cur:
        new_node = old_to_new[cur]
        new_node.next = old_to_new.get(cur.next)
        new_node.random = old_to_new.get(cur.random)
        cur = cur.next

    return old_to_new[head]
