class Node:
    def __init__(self, key):
        self.data = key
        self.next = None

def findTriplet( head, n, x):
    if n<3:
        return False
    s = set()
    curr = head
    while curr!=None:
        rem = x-curr.data
        if rem in s:
            return True
        s.add(curr.data)
        curr = curr.next
    return False
