class Node:
    def __init__(self, data):
        self.data = data
        self.prev = None
        self.next = None

class DoublyLinkedList:
    def __init__(self):
        self.head = None

    def append(self, data):
        new_node = Node(data)
        if not self.head:
            self.head = new_node
            return
        current_node = self.head
        while current_node.next:
            current_node = current_node.next
        current_node.next = new_node
        new_node.prev = current_node

    def quicksort(self):
        self.head = self._quicksort(self.head)

    def _quicksort(self, node):
        if not node or not node.next:
            return node
        pivot = node.data
        left = right = node
        while right.next:
            if right.next.data < pivot:
                left = left.next
            right = right.next
        left.prev.next = right
        right.prev = left.prev
        left.prev = None
        left.next = None
        left = self._quicksort(left)
        right = self._quicksort(right)
        new_head = None
        current = None
        while left:
            new_head = left
            left = left.next
            new_head.next = current
            current = new_head
        new_head.next = right
        for current_node in [current, new_head.next]:
            if current_node:
                current_node.prev = current_node.prev
        return new_head
