class Node:
    def __init__(self, data):
        self.data = data
        self.next = None

def insert_sorted(head, data):
    new_node = Node(data)
    if not head:
        new_node.next = new_node
        return new_node
    if head.data >= data:
        curr = head
        while curr.next != head:
            curr = curr.next
        curr.next = new_node
        new_node.next = head
        return new_node
    curr = head
    while curr.next != head and curr.next.data < data:
        curr = curr.next
    new_node.next = curr.next
    curr.next = new_node
    return head
