class Node:
    def __init__(self, data):
        self.data = data
        self.next = None

class LinkedList:
    def __init__(self):
        self.head = None

    def split_list(self):
        if self.head is None:
            return None

        slow_ptr = self.head
        fast_ptr = self.head

        while fast_ptr.next != self.head and fast_ptr.next.next != self.head:
            slow_ptr = slow_ptr.next
            fast_ptr = fast_ptr.next.next

        if fast_ptr.next.next == self.head:
            fast_ptr = fast_ptr.next

        first_head = self.head
        second_head = slow_ptr.next

        slow_ptr.next = first_head
        fast_ptr.next = second_head

        return first_head, second_head
