def exp_search(arr, x):
    index = 1
    while index < len(arr) and arr[index] <= x:
        index *= 2
    left = index // 2
    right = min(index, len(arr)) - 1
    while left <= right:
        mid = left + (right - left) // 2
        if arr[mid] < x:
            left = mid + 1
        elif arr[mid] > x:
            right = mid - 1
        else:
            return mid
    return -1

arr = [2, 3, 4, 10, 40]
x = 10
result = exp_search(arr, x)
if result!= -1:
    print("Element found at index", str(result))
else:
    print("Element not found in the array")
