def is_operator(c):
    return c in '+-*/'

def precedence(c):
    if c == '+' or c == '-':
        return 1
    elif c == '*' or c == '/':
        return 2
    else:
        return 0

def infix_to_postfix(infix):
    postfix = []
    stack = []
    for c in infix:
        if c.isalnum():
            postfix.append(c)
        elif c == '(':
            stack.append(c)
        elif c == ')':
            while stack and stack[-1]!= '(':
                postfix.append(stack.pop())
            stack.pop()
        else:
            while stack and stack[-1]!= '(' and precedence(c) <= precedence(stack[-1]):
                postfix.append(stack.pop())
            stack.append(c)
    while stack:
        postfix.append(stack.pop())
    return ' '.join(postfix)

print(infix_to_postfix("A+B*C"))
