def sort_stack(stack):
    if len(stack) > 0:
        temp = stack.pop()
        sort_stack(stack)
        sorted_insert(stack, temp)

def sorted_insert(stack, val):
    if len(stack) == 0 or val > stack[-1]:
        stack.append(val)
    else:
        temp = stack.pop()
        sorted_insert(stack, val)
        stack.append(temp)
