class Stack:
    def __init__(self):
        self.stack = []
        self.increment = 0

    def push(self, x):
        self.stack.append(x + self.increment)

    def pop(self):
        return self.stack.pop() - self.increment

    def top(self):
        return self.stack[-1] - self.increment

    def increment(self, x):
        self.increment += x
