import re
from collections import deque

def decode_yaz(string):
    stack = deque()
    for i in reversed(re.findall(r'[A-Z]+|\d+', string)):
        if i.isdigit():
            stack.extendleft(int(i) * [stack.pop()])
        else:
            stack.appendleft(i)
    return ''.join(stack)

