def remove_outermost_parentheses(s):
    stack = []
    result = ''
    for char in s:
        if char == '(':
            stack.append(char)
        elif char == ')':
            if stack:
                stack.pop()
            else:
                result += char
        else:
            if stack:
                result += char
            else:
                result += '(' + char + ')'
    return result if stack else result[:-1]
