from collections import deque

def online_stock_span(stock_prices):
    span = []
    s = deque()
    for price in stock_prices:
        while s and stock_prices[s[-1]] <= price:
            s.pop()
        span.append(len(s) + 1) if s else span.append(1)
        s.append(len(stock_prices) - len(span))
    return span
