def sumSubarrayMins(arr):
    n=len(arr)
    left,right,ans=[-1]*n,[n]*n,0
    stack=[]
    for i in range(n):
        while stack and arr[stack[-1]]>=arr[i]:
            stack.pop()
        left[i]=stack[-1] if stack else -1
        stack.append(i)
    stack=[]
    for i in range(n-1,-1,-1):
        while stack and arr[stack[-1]]>arr[i]:
            stack.pop()
        right[i]=stack[-1] if stack else n
        stack.append(i)
    for i in range(n):
        ans+=(i-left[i])*(right[i]-i)*arr[i]
    return ans%(10**9+7)
