class Stack:
    def __init__(self):
        self.items = []

    def is_empty(self):
        return not bool(self.items)

    def push(self, item):
        self.items.append(item)

    def pop(self):
        return self.items.pop()

    def peek(self):
        return self.items[-1]

    def size(self):
        return len(self.items)


def asteroid_collision(asteroids):
    stack = Stack()
    for ast in asteroids:
        if not stack.is_empty():
            while stack.size() > 0 and stack.peek() > 0 and ast < 0:
                if stack.peek() + ast < 0:
                    stack.pop()
                elif stack.peek() + ast == 0:
                    stack.pop()
                    ast = 0
                    break
                else:
                    ast = 0
                    break
        if ast != 0:
            stack.push(ast)
    return stack.items


assert asteroid_collision([5, 10, -5]) == [5, 10]
assert asteroid_collision([8, -8]) == []
assert asteroid_collision([10, 2, -5]) == [10]
assert asteroid_collision([-2, -1, 1, 2]) == [-2, -1, 1, 2]
