class Car:
    def __init__(self, position, speed):
        self.position = position
        self.speed = speed

def car_fleet(target, positions, speeds):
    cars = [Car(p, s) for p, s in zip(positions, speeds)]
    cars.sort(key=lambda c: -c.position)
    stack = []
    for car in cars:
        time = (target - car.position) / car.speed
        while stack and time >= stack[-1]:
            stack.pop()
        stack.append(time)
    return len(stack)
