from collections import deque

def slidingWindowMax(nums, k):
    result = []
    dq = deque()

    for i in range(len(nums)):
        while dq and nums[i] >= nums[dq[-1]]:
            dq.pop()

        dq.append(i)

        if dq[0] == i - k:
            dq.popleft()

        if i >= k - 1:
            result.append(nums[dq[0]])

    return result
