class CircularDeque:
    def __init__(self, k: int):
        self.max_size = k
        self.front = 0
        self.rear = 0
        self.deque = [0]*k

    def insertFront(self, value: int) -> bool:
        if (self.front == 0):
            if self.deque[self.front]!= 0:
                return False
        elif (self.front == self.max_size):
            if self.deque[self.front-1]!= 0:
                return False
        self.deque[(self.front-1)%self.max_size] = value
        self.front = (self.front-1)%self.max_size
        return True

    def insertRear(self, value: int) -> bool:
        if (self.rear == self.max_size-1):
            if self.deque[self.rear]!= 0:
                return False
        elif (self.rear == 0):
            if self.deque[self.rear+1]!= 0:
                return False
        self.deque[(self.rear+1)%self.max_size] = value
        self.rear = (self.rear+1)%self.max_size
        return True

    def deleteFront(self) -> bool:
        if self.front == self.rear:
            return False
        self.deque[self.front] = 0
        if self.front == self.max_size-1:
            self.front = 0
        else:
            self.front += 1
        return True

    def deleteRear(self) -> bool:
        if self.front == self.rear:
            return False
        self.deque[self.rear-1] = 0
        if self.rear == 1:
            self.rear = self.max_size-1
        else:
            self.rear -= 1
        return True

    def getFront(self) -> int:
        return self.deque[self.front]

    def getRear(self) -> int:
        return self.deque[self.rear-1]
