from collections import deque

class MovingAverage:
    def __init__(self, size):
        self.queue = deque()
        self.window_size = size

    def next(self, val):
        self.queue.append(val)
        if len(self.queue) > self.window_size:
            self.queue.popleft()
        return sum(self.queue) / len(self.queue)
