from collections import deque

def max_eq(arr, k):
    n = len(arr)
    dq = deque()

    for i in range(n):
        while dq and dq[-1][1] <= i - k:
            dq.pop()

        while dq and dq[0][0] <= arr[i]:
            dq.popleft()

        dq.append((arr[i], i))

        if i >= k - 1:
            print(dq[0][0], end=" ")

arr = [10, 5, 2, 7, 8, 7]
k = 3

max_eq(arr, k)
