from collections import deque

def maxSlidingWindow(nums, k):
    if not nums:
        return []

    n = len(nums)
    res = []
    dq = deque()

    for i in range(n):
        if dq and dq[0] < i - k + 1:
            dq.popleft()
        while dq and nums[dq[-1]] < nums[i]:
            dq.pop()
        dq.append(i)

        if i >= k - 1:
            res.append(nums[dq[0]])

    return res
