class Node:
    def __init__(self, key):
        self.left = None
        self.right = None
        self.val = key

class BinaryTree:
    def depth(self, node):
        if node is None:
            return 0
        else:
            left_depth = self.depth(node.left)
            right_depth = self.depth(node.right)

            if left_depth > right_depth:
                return left_depth + 1
            else:
                return right_depth + 1
