def inorder_iterative(root):
    if not root:
        return []

    stack = []
    result = []
    current = root

    while True:
        while current:
            stack.append(current)
            current = current.left
        if stack:
            current = stack.pop()
            result.append(current.val)
            current = current.right
        else:
            break

    return result

def inorder_recursive(root):
    result = []
    def inorder(node):
        if node:
            inorder(node.left)
            result.append(node.val)
            inorder(node.right)
    inorder(root)
    return result
