class Solution:
    def zigzagLevelOrder(self, root: 'TreeNode') -> 'List[List[int]]':
        if not root:
            return []

        result = []
        isReverse = False

        def dfs(node, level):
            if len(result) <= level:
                result.append([])
            result[level].extend([node.val])
            if level > 0 and not isReverse:
                isReverse = True
            elif level > 0 and isReverse:
                isReverse = False
            if node.left:
                dfs(node.left, level + 1)
            if node.right:
                dfs(node.right, level + 1)

        dfs(root, 0)
        return [row for row in result]
