def diameterOfBinaryTree(root):
    self.diameter = 0
    def maxDepth(node):
        if node is None:
            return 0
        left = maxDepth(node.left)
        right = maxDepth(node.right)
        self.length = max(left, right) + 1
        return self.length
    def postorder(node):
        if node:
            postorder(node.left)
            self.length = maxDepth(node.right)
            if self.length + self.length - 1 > self.diameter:
                self.diameter = self.length + self.length - 1
            postorder(node.right)
    postorder(root)
    return self.diameter
