def path_sum_checker(tree, target):
    def _path_sum_checker(node, current_sum):
        if not node:
            return False

        current_sum += node.val

        if not node.left and not node.right and current_sum == target:
            return True

        return _path_sum_checker(node.left, current_sum) or _path_sum_checker(node.right, current_sum)

    return _path_sum_checker(tree.root, 0)
