class TreeNode:
    def __init__(self, key):
        self.val = key
        self.left = None
        self.right = None

def deleteNode(root, key):
    if not root:
        return root
    if key < root.val:
        root.left = deleteNode(root.left, key)
    elif key > root.val:
        root.right = deleteNode(root.right, key)
    else:
        if not root.left:
            return root.right
        elif not root.right:
            return root.left
        temp = root.right
        min_val = temp.val
        while temp.left:
            temp = temp.left
            min_val = temp.val
        root.val = min_val
        root.right = deleteNode(root.right, root.val)
    return root
