def bitap(pattern, text):
    m = len(pattern)
    n = len(text)
    pi = [0] * m
    pi[0] = m - 1
    for i in range(1, m):
        if pattern[i] == pattern[m - 1 - i]:
            pi[i] = pi[i - 1] - 1
        else:
            pi[i] = pi[i - 1]
    d = [0] * (m + n)
    for i in range(m, m + n):
        if pattern[m - 1 - d[i - m]]!= text[i - 1]:
            d[i] = d[i - 1] + 1
        else:
            d[i] = d[i - 1]
    result = []
    for i in range(m + n - 1, -1, -1):
        if d[i] == 0:
            result.append(i - m + 1)
        d[i - 1] = max(d[i - 1], d[i] - (m - 1 - pi[d[i]]))
    result.reverse()
    return result
