class TreeNode:
    def __init__(self, val=0, left=None, right=None):
        self.val = val
        self.left = left
        self.right = right

def recoverTree(root):
    stack = []
    prev = None
    first = None
    second = None

    while root or stack:
        while root:
            stack.append(root)
            root = root.left

        root = stack.pop()
        if prev and prev.val > root.val:
            if first is None:
                first = prev
            second = root
        prev = root
        root = root.right

    first.val, second.val = second.val, first.val
