class TreeNode:
    def __init__(self, x, left=None, right=None):
        self.val = x
        self.left = left
        self.right = right

def flatten(root: TreeNode) -> None:
    if not root:
        return None

    left_tail = root.left
    while left_tail.right:
        left_tail = left_tail.right

    if root.left:
        left_tail.right = root.right
        root.right = root.left
        root.left = None

    flatten(root.right)
