class Node:
    def __init__(self, val, next=None):
        self.val = val
        self.next = next

def connect(root):
    if root is None:
        return

    prev = None

    while root:
        current = root
        while current:
            for node in [current, current.next, current.next.next]:
                if node:
                    if prev:
                        prev.next = node
                    prev = node
            current = current.next
        prev = None
        root = root.next
