class TreeNode:
    def __init__(self, val=0, left=None, right=None):
        self.val = val
        self.left = left
        self.right = right

def maxPathSum(root):
    max_sum = float('-inf')
    def max_gain(node):
        nonlocal max_sum
        if not node:
            return 0
        left_gain = max(max_gain(node.left), 0)
        right_gain = max(max_gain(node.right), 0)
        new_price = node.val + left_gain + right_gain
        max_sum = max(max_sum, new_price)
        return node.val + max(left_gain, right_gain)
    max_gain(root)
    return max_sum
