        self.right = None

def morris_traversal(root):
    current = root
    while current:
        if not current.left:
            print(current.val)
            current = current.right
        else:
            pre = current.left
            while pre.right and pre.right != current:
                pre = pre.right
            if not pre.right:
                pre.right = current
                current = current.left
            else:
