class MinHeap:
    def __init__(self):
        self.heap = []

    def parent(self, i):
        return (i - 1) // 2

    def left_child(self, i):
        return 2 * i + 1

    def right_child(self, i):
        return 2 * i + 2

    def has_left_child(self, i):
        return self.left_child(i) < len(self.heap)

    def has_right_child(self, i):
        return self.right_child(i) < len(self.heap)

    def swap(self, i, j):
        self.heap[i], self.heap[j] = self.heap[j], self.heap[i]

    def min_heapify(self, i):
        smallest = i
        if self.has_left_child(i) and self.heap[self.left_child(i)] < self.heap[smallest]:
            smallest = self.left_child(i)
        if self.has_right_child(i) and self.heap[self.right_child(i)] < self.heap[smallest]:
            smallest = self.right_child(i)
        if smallest != i:
            self.swap(i, smallest)
            self.min_heapify(smallest)

    def insert(self, value):
        self.heap.append(value)
        self.min_heapify(len(self.heap) - 1)

    def get_min(self):
        return self.heap[0]

    def delete_min(self):
        self.heap[0] = self.heap.pop()
        self.min_heapify(0)

    def build_heap(self, arr):
        self.heap = arr
        for i in range(len(self.heap) // 2 - 1, -1, -1):
            self.min_heapify(i)
