class MaxHeap:
    def __init__(self):
        self.heap = []

    def parent(self, idx):
        return (idx - 1) // 2

    def left_child(self, idx):
        return 2 * idx + 1

    def right_child(self, idx):
        return 2 * idx + 2

    def max_heapify(self, idx):
        max_idx = idx
        left_child = self.left_child(idx)
        right_child = self.right_child(idx)

        if left_child < len(self.heap) and self.heap[left_child] > self.heap[max_idx]:
            max_idx = left_child

        if right_child < len(self.heap) and self.heap[right_child] > self.heap[max_idx]:
            max_idx = right_child

        if max_idx != idx:
            self.heap[idx], self.heap[max_idx] = self.heap[max_idx], self.heap[idx]
            self.max_heapify(max_idx)

    def insert(self, val):
        self.heap.append(val)
        idx = len(self.heap) - 1
        parent = self.parent(idx)

        while idx > 0 and self.heap[parent] < self.heap[idx]:
            self.heap[parent], self.heap[idx] = self.heap[idx], self.heap[parent]
            idx = parent
            parent = self.parent(idx)

    def delete_max(self):
        if len(self.heap) == 0:
            return None
        if len(self.heap) == 1:
            return self.heap.pop()

        max_val = self.heap[0]
        self.heap[0] = self.heap.pop()
        self.max_heapify(0)
        return max_val

    def peek_max(self):
        if len(self.heap) == 0:
            return None
        return self.heap[0]

    def is_empty(self):
        return len(self.heap) == 0

    def size(self):
        return len(self.heap)

