import heapq

def reorganize_string(s):
    char_count = {}
    for char in s:
        char_count[char] = char_count.get(char, 0) + 1

    min_heap = [(-count, char) for char, count in char_count.items()]
    heapq.heapify(min_heap)

    result = []
    while min_heap:
        count, char = heapq.heappop(min_heap)
        count = -count

        result.append(char)
        if count < len(result) // 2:
            heapq.heappush(min_heap, (1, char))
        if min_heap:
            count2, char2 = min_heap[0]
            if -count2 < len(result) // 2:
                heapq.heappush(min_heap, (count, char))
                min_heap.sort(key=lambda x: -x[0])
                heapq.heappop(min_heap)
            else:
                heapq.heappush(min_heap, (count2, char2))

    return ''.join(result)

