import heapq

def kth_smallest(matrix, k):
    n = len(matrix)
    heap = [(matrix[i][0], i, 0) for i in range(n)]
    heapq.heapify(heap)
    for _ in range(k-1):
        _, i, j = heapq.heappop(heap)
        if j+1 < n:
            heapq.heappush(heap, (matrix[i][j+1], i, j+1))
    return heapq.heappop(heap)[0]
