import heapq

def distant_barcodes(barcode, distance):
    n = len(barcode)
    heap = [(0, i) for i in range(n)]
    heapq.heapify(heap)
    res = [-1]*n
    while heap:
        d, i = heapq.heappop(heap)
        if res[i] == -1:
            res[i] = d
            for j in range(n):
                if barcode[j]!= barcode[i]:
                    heapq.heappush(heap, (d+distance, j))
    return res
