def majority_element(nums):
    count, candidate = 0, None
    for num in nums:
        if count == 0:
            candidate, count = num, 1
        else:
            if candidate == num:
                count += 1
            else:
                count -= 1
    return candidate

print(majority_element([3,2,3]))
print(majority_element([2,2,1,1,1,2,2]))
