import heapq

def cheapest_flights(n, flights, src, dst, k):
    graph = [[] for _ in range(n + 1)]
    for u, v, w in flights:
        graph[u].append((v, w))

    pq = [(0, src, k + 1)]
    seen = {src: 0}
    while pq:
        price, node, stops = heapq.heappop(pq)
        if node == dst:
            return price
        if stops > k + 1 or price > seen.get(node, float('inf')):
            continue
        for nei, weight in graph[node]:
            new_price = price + weight
            if nei not in seen or new_price < seen[nei]:
                seen[nei] = new_price
                heapq.heappush(pq, (new_price, nei, stops + 1))
    return -1
