import heapq

def networkDelayTime(times, n, k):
    graph = [[] for _ in range(n)]
    for u, v, w in times:
        graph[u-1].append((v-1, w))
    dist = [float('inf')] * n
    dist[k-1] = 0
    heap = [(0, k-1)]
    while heap:
        d, u = heapq.heappop(heap)
        if d > dist[u]:
            continue
        for v, w in graph[u]:
            new_dist = dist[u] + w
            if new_dist < dist[v]:
                dist[v] = new_dist
                heapq.heappush(heap, (new_dist, v))
    ans = max(dist)
    return ans if ans < float('inf') else -1
