import heapq

def furthest_building(floor_heights, bricks, ladders):
    pq = []
    for i in range(len(floor_heights) - 1):
        diff = floor_heights[i + 1] - floor_heights[i]
        if diff > 0:
            heapq.heappush(pq, diff)
        if len(pq) > ladders:
            bricks -= heapq.heappop(pq)
        if bricks < 0:
            return i
    return len(floor_heights) - 1

# Example usage:
print(furthest_building([4, 2, 7, 6, 5, 9, 4, 3, 11], 5, 3))  # Output: 7
