def isIsomorphic(s, t):
    if len(s) != len(t):
        return False
    s2t, t2s = {}, {}
    for i in range(len(s)):
        if s[i] in s2t:
            if s2t[s[i]] != t[i]:
                return False
        else:
            if t[i] in t2s:
                return False
            s2t[s[i]] = t[i]
            t2s[t[i]] = s[i]
    return True
