def wordPattern(pattern, s):
    words = s.split()
    if len(pattern) != len(words):
        return False

    char_map = {}
    word_map = {}

    for char, word in zip(pattern, words):
        if char not in char_map:
            char_map[char] = word
        if word not in word_map:
            word_map[word] = char

        if char_map[char] != word or word_map[word] != char:
            return False

    return True

pattern = "abba"
s = "dog cat cat dog"
print(wordPattern(pattern, s))

