class InsertDeleteGetRandomO1:
    def __init__(self):
        self.hash_map = {}
        self.list = []

    def insert(self, val):
        if val not in self.hash_map:
            self.list.append(val)
            self.hash_map[val] = len(self.list) - 1

    def remove(self, val):
        if val in self.hash_map:
            idx = self.hash_map.pop(val)
            if idx != len(self.list) - 1:
                self.list[idx] = self.list.pop()
                self.hash_map[self.list[idx]] = idx

    def getRandom(self):
        return self.list[random.randint(0, len(self.list) - 1)]

    def getRandomO1(self):
