class HashSet:
    def __init__(self):
        self.bucket_size = 10
        self.buckets = [[] for _ in range(self.bucket_size)]

    def _hash(self, key):
        hash_value = sum(ord(char) for char in key) % self.bucket_size
        return hash_value

    def add(self, key):
        hash_value = self._hash(key)
        if key not in self.buckets[hash_value]:
            self.buckets[hash_value].append(key)

    def remove(self, key):
        hash_value = self._hash(key)
        if key in self.buckets[hash_value]:
            self.buckets[hash_value].remove(key)

    def contains(self, key):
        hash_value = self._hash(key)
        return key in self.buckets[hash_value]


# Usage example
hash_set = HashSet()
hash_set.add("apple")
hash_set.add("banana")
print(hash_set.contains("banana"))  # True
hash_set.remove("apple")
print(hash_set.contains("apple"))  # False
