def check_ransom_note(ransom_note, magazine):
    ransom_dict = {}
    magazine_dict = {}

    for word in ransom_note.split():
        ransom_dict[word] = ransom_dict.get(word, 0) + 1

    for word in magazine.split():
        magazine_dict[word] = magazine_dict.get(word, 0) + 1

    for word, count in ransom_dict.items():
        if word not in magazine_dict or magazine_dict[word] < count:
            return False

    return True

# Example usage
ransom_note = "hello world"
magazine = "hello python world"
print(check_ransom_note(ransom_note, magazine))
