def search_bitonic(arr, target):
n = len(arr)
if n == 1:
return 0 if arr[0] == target else -1

peak_index = find_peak(arr)
if target == arr[peak_index]:
return peak_index

result = -1
if peak_index != 0:
result = binary_search(arr[:peak_index], target)
if result == -1 and peak_index != n-1:
result = binary_search(arr[peak_index+1:], target)

return result

def find_peak(arr):
left, right = 0, len(arr) - 1
while left < right:
mid = (left + right) // 2
if arr[mid] > arr[mid+1]:
right = mid
else:
left = mid + 1
return left

def binary_search(arr, target):
low, high = 0, len(arr) - 1
while low <= high:
mid = (low + high) // 2
if arr[mid] == target:
return mid
elif arr[mid] < target:
low = mid + 1
else:
high = mid - 1
return -1
