    def __init__(self):
        self.children = defaultdict(Trie)
        self.word = None

def search(board, word):
    trie = Trie()
    for word in words:
        cur = trie
        for char in word:
            cur = cur.children[char]
        cur.word = word

    def dfs(board, i, j, cur):
        if i < 0 or i >= m or j < 0 or j >= n or board[i][j] not in cur.children:
            return False

        cur = cur.children[board[i][j]]
        if cur.word is not None:
            found.add(cur.word)

        temp = board[i][j]
        board[i][j] = '#'
        for di, dj in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            if dfs(board, i + di, j + dj, cur):
                return True
        board[i][j] = temp
        return False

    found = set()
    m, n = len(board), len(board[0])
    for i in range(m):
        for j in range(n):
            if board[i][j] in trie.children:
