class TrieNode:
    def __init__(self):
        self.children = {}
        self.is_end_of_word = False

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.is_end_of_word = True

    def replace_words(self, text):
        result = []
        node = self.root
        start = 0
        for end, char in enumerate(text):
            if char not in node.children:
                if start != end:
                    result.append(text[start:end])
                result.append(text[end])
                start = end + 1
                node = self.root
            else:
                node = node.children[char]
        if start != end:
            result.append(text[start:end+1])
        else:
            result.append(text)
        for node in self.root.children.values():
            self._replace_words(node, result, text)
        return [''.join(map(str, r)) for r in result]

    def _replace_words(self, node, result, text):
        if node.is_end_of_word:
            result.append(text[:len(result[-1])])
            result[-1] += ''
        for char, n in node.children.items():
            self._replace_words(n, result, text)
